class t{static info(){return uni.$u.http.get("user/info")}static center(){return uni.$u.http.get("user/center")}static edit(t){const e={scene:t.scene,value:t.value};return uni.$u.http.post("user/edit",e)}static forgetPwd(t){const e={code:t.code,mobile:t.mobile,password:t.newPassword};return uni.$u.http.post("user/forgetPwd",e)}static changePwd(t){const e={newPassword:t.newPassword,oldPassword:t.oldPassword};return uni.$u.http.post("user/changePwd",e)}static bindWeChat(t){return uni.$u.http.post("user/bindWeChat",{code:t})}static bindMobile(t){const e={mobile:t.mobile,code:t.code,type:t.type};return uni.$u.http.post("user/bindMobile",e)}static bindEmail(t){const e={email:t.email,code:t.code};return uni.$u.http.post("user/bindEmail",e)}}export{t as u};
